// Import dependencies
import {
  Finger,
  FingerCurl,
  FingerDirection,
  GestureDescription,
} from "fingerpose";

// Define Gesture Description
export const oneGesture = new GestureDescription("one");

// index
thumbsDownGesture.addCurl(Finger.Index, FingerCurl.NoCurl);
thumbsDownGesture.addDirection(Finger.Thumb, FingerDirection.VerticalUp, 1.0);
thumbsDownGesture.addDirection(
  Finger.Index,
  FingerDirection.DiagonalUpLeft,
  0.9
);
thumbsDownGesture.addDirection(
  Finger.Index,
  FingerDirection.DiagonalUpRight,
  0.9
);

// rest of the fingers
for (let finger of [Finger.Thumb, Finger.Middle, Finger.Ring, Finger.Pinky]) {
  thumbsDownGesture.addCurl(finger, FingerCurl.FullCurl, 1.0);
}
