// Import dependencies
import {
    Finger,
    FingerCurl,
    GestureDescription,
  } from "fingerpose";

  export const palmGesture = new GestureDescription("palm");

  // rest of the fingers
  for (let finger of [
    Finger.Thumb,
    Finger.Index,
    Finger.Middle,
    Finger.Ring,
    Finger.Pinky,
  ]) {
    fistGesture.addCurl(finger, FingerCurl.NoCurl, 1.0);
    fistGesture.addCurl(finger, FingerCurl.HalfCurl, 0.7);
  }
