};

export const actions = (gesture, action, posts) => {
  const name = String(gesture);
  const searchParams = new URLSearchParams(window.location.search);
  // get the number of the page
  let count = Number(searchParams.get("page"));
  //console.log(name);
  //console.log(action);
  //console.log(posts);

  switch (name) {
    // go to sign in
    case "":
      window.location.href = "http://localhost:3000/auth";
      break;

    // go to main page
    case "palm":
      window.location.href = "http://localhost:3000/posts";
      break;

    // go ahead to a page
    case "right":
      if (count === 0) {
        window.location.href = "http://localhost:3000/posts?page=2";
      } else {
        count++;
        window.location.href = `http://localhost:3000/posts?page=${count}`;
      }
      break;

    // go back to a page
    case "left":
      if (count === 2) {
        window.location.href = "http://localhost:3000/posts";
      } else {
        count--;
        window.location.href = `http://localhost:3000/posts?page=${count}`;
      }
      break;

    // switch auth forms
    case "i_love_you":
      if (window.location.href === "http://localhost:3000/auth") action();
      break;

    // submit
    case "":
      break;

    // view first post of the page post
    case "one":
      break;

    default:
      break;
  }
};
